CREATE TABLE "MIS_WAREHOUSE"."DAILY_VOLT_METRICS" (
    "ID" NUMBER GENERATED BY DEFAULT ON NULL AS IDENTITY,
    "DATA_TIME" DATE NOT NULL ENABLE,
    "ENTITY_NAME" VARCHAR2(100 BYTE) NOT NULL ENABLE,
    "VOLT_LEVEL" NUMBER(10, 0) NOT NULL,
    "METRIC_NAME" VARCHAR2(100 BYTE) NOT NULL ENABLE,
    "DATA_VAL" VARCHAR2(100 BYTE) NOT NULL ENABLE,
    UNIQUE ("DATA_TIME", "ENTITY_NAME", "METRIC_NAME", "VOLT_LEVEL")
)